class App extends Component {
  //Class version of the above code
  constructor() {
    super();

    this.state = {
      // stuff in here is available globally
      //JSON object
      monsters: [],
      searchField: "",
    };
  }

  componentDidMount() {
    // when the component gets rendered for the first time
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) =>
        this.setState(() => {
          return { monsters: users };
        })
      );
  }

  onSearchChange = (event) => {
    const searchField = event.target.value.toLocaleLowerCase();
    // [{name: 'Leanne'}, {name: 'Eric'}]
    this.setState(() => {
      // if the name is the same, it will update the key with the value: event.target.value.toLocaleLowerCase();
      return { searchField };
    });
  };

  render() {
    console.log("render");

    const { monsters, searchField } = this.state; // makes it so that i dont need to call this.state every time
    const { onSearchChange } = this;

    const filteredMonsters = monsters.filter((el) => {
      return el.name.toLocaleLowerCase().includes(searchField);
    });

    return (
      <div className="App">
        <input
          className="search-box"
          type="search"
          placeholder="search monsters"
          onChange={onSearchChange}
        />
        {/* {filteredMonsters.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
          );
        })} */}
        <CardList monsters={filteredMonsters} />
      </div>
    );
  }
}

export default App;
