class App extends Component { //Class version of the above code
  constructor() {
    super();

    this.state = { //JSON object
      name: {firstName: 'Eric', lastName: 'Kuo'},
      company: 'SPI'
    }
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi {this.state.name}, I work at {this.state.company}
          </p>
          <button onClick={() => {
            this.setState({ name: 'Bob' })
            console.log(this.state)
          }}>
            Change Name
          </button>
        </header>
      </div>
    );
  }
}


export default App;
